package basics;

public class DoWhileExample_29_11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
				int x = 0;
				while (x > 0) {
					System.out.println("while loop" +x--);
				}
				do {
					System.out.println("do-while loop" + x++);
				}while (x > 0);
		}
	
}



